import React, {useEffect, useState} from 'react';
import {HEADINGS} from "./headings";
// import FilteringTable from "./FilteringTable";
import DataTable from "./DataTable";
import "./table.css";

function ListPods() {
    const [podDetails, setPodDetails] = useState([]);

    const headings = [
        'Name',
        'NameSpace',
        'Status',
        'Age',
      ];

    useEffect(()=> {
        fetch("http://localhost:3001/").then(response => response.json())
        .then(data => {
            console.log("data : ", data);
            console.log("==data==", data)
            const result = data.map(({ name, nameSpace, status, age }) => [name, nameSpace, status, age]);
            console.log("==result==", result)
            const rows = result
            setPodDetails(rows)
        });
    }, [])



    return(
        <div>
            List pods
            <DataTable headings={HEADINGS} rows={podDetails} />
{/*             
            <FilteringTable podDetails = {podDetails}/>
            <DataTable headings={headings} rows={rows} /> */}
        </div>
    )
}

export default ListPods;
