import React, {useEffect, useState} from 'react';
import {HEADINGS} from "./headings";
// import FilteringTable from "./FilteringTable";
import DataTable from "./DataTable";
import "./table.css";

function ListPods() {
    const [podDetails, setPodDetails] = useState([]);
    const [row, setRowData] = useState([]);
    const [filter, setFilter] = useState("");

    function handleChange(val) {
        //how to clear filter
        // or when nothing matches
        setFilter(val);

        let filteredRows = [];
        if(val.trim() !== "") {
            filteredRows = podDetails.filter((row) => row.name.toLowerCase().indexOf(val.toLowerCase()) > -1);
            setPodDetails(filteredRows);
            const result = filteredRows.map(({ name, nameSpace, status, age }) => [name, nameSpace, status, age]);
            setRowData(result);
        }else {
            setPodDetails(podDetails);
            setRowData(row);
        }

    }

    useEffect(()=> {
        fetch("http://localhost:3001/").then(response => response.json())
        .then(data => {
            setPodDetails(data)

            const result = data.map(({ name, nameSpace, status, age }) => [name, nameSpace, status, age]);
            setRowData(result)
        });
    }, [])



    return(
        <div>
            Pods
            <input value = {filter || ""} onChange={e => handleChange(e.target.value)}/>
            <DataTable headings={HEADINGS} rows={row} />
        </div>
    )
}

export default ListPods;
